import {icon} from "./images/icon.js"

"use strict"

export const tool = {
  setWorkbenchButton: {
    svg: null,
    execute: function(){
      const svg= document.createElement("div")
      this.svg = svg
      svg.innerHTML= icon.update
      svg.style.position= "absolute"
      svg.style.top = "16px"
      svg.style.right = "130px"
      svg.style.opacity = "1"
      svg.style.cursor = "pointer"
      document.body.appendChild(svg)
      svg.onclick =  this.click
    },
    click: function(e){
      const fireEvent = "Workbench.update.fire"
      const evf = new CustomEvent(fireEvent)
      document.dispatchEvent(evf)
    },
  },
}
